import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import clienteAxios from "../config/clientAxios";
import LayoutNotAuth from "../components/layout/LayoutNotAuth";
import { Row, Col, Image } from "antd";

function HomeId() {
  let { id } = useParams();

  const [data, setData] = useState([] as any);
  const [test, setTest] = useState(true);
  const [visible, setVisible] = useState(false);

  const getData = async () => {
    try {
      const res: any = await clienteAxios.get(`/data/${id}`);

      console.log(res)

      const response: any = res.data;

      setData(response);
      if (!response?.data) {
        setTest(false);
      }
    } catch (error) {
      console.log(error);
    }
  };

  console.log(data);

  useEffect(() => {
    if (test === true) {
      getData();
    }
  }, [test]);

  return (
    <LayoutNotAuth>
      <Row style={{ padding: "20px" }}>
        <Col span={8} style={{ display: "flex" }}>
          {data[0]?.data.image === "" ? (
            <Image
              width={200}
              height={200}
              src="error"
              fallback="data:image/png;base64,"
            />
          ) : (
            <Image
              preview={{ visible: false }}
              width={200}
              src={data[0]?.datae}
              onClick={() => setVisible(true)}
            />
          )}
        </Col>

        <Col span={16}>
          {" "}
          <h2>Title: {data[0]?.data.title}</h2>
          <h4> {data[0]?.data.body}</h4>
          <p>Author: {`${data[0]?.user.name} ${data[0]?.user.lastName}    `}</p>
        </Col>
      </Row>
    </LayoutNotAuth>
  );
}

export default HomeId;
